import React, { useState } from "react";
import Button from "./UI/Button/Button";
import { styled } from "styled-components";
const Icon = styled.i`
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding-right: 8px;
  color: #fff;
`;

export default function NavBarItem(props) {
  const [boder, setBorder] = useState(props.active);

  const handlerClick = () => {
    if (border === true) {
      setBorder(false);
    } else if (border === false) setBorder(true);
  };

  return (
    <Button active={boder} onClick={handlerClick}>
      <Icon>
        <i className={`fa ${props.icon}`}></i>
      </Icon>
      <p>{props.type}</p>
    </Button>
  );
}
