import React, { useState } from "react";
import { styled } from "styled-components";

const Div = styled.div`
  border: 1px solid #fff;
  border-radius: 20px;
  color: #fff;
  display: flex;
  align-items: center;
  padding: 8px;
  margin: 20px;

  & i {
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding-right: 8px;
  }
`;

export default function NavBarItem(props) {
  const [isValid, setIsValid] = useState(props.active);

  console.log(isValid);
  return (
    <Div>
      <i class={`fa ${props.icon}`}></i>
      <p>{props.type}</p>
    </Div>
  );
}
