import React from "react";
import { styled } from "styled-components";
import UIButton from "../../../elements/UIButton";
import DataPicker from "./DataPicker";
const StyledForm = styled.div`
  display: flex;
  justify-content: space-around;
  background-color: #fff;
  padding: 10px 20px;
  position: relative;
  top: 140px;
  border: 4px solid orange;
  border-radius: 5px;

  & label {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  & label:nth-child(2) {
    display: flex;
    flex-direction: column;
  }

  .input-icon {
    display: flex;
    align-items: center;
    position: relative;
  }

  .input-icon:hover .date-picker {
    display: block;
  }

  & input {
    border: none;
    width: 100px;
    outline: none;
    width: 200px;
    padding: 10px 30px 10px 15px;
  }

  & input::placeholder {
    font-weight: 500;
    font-size: 16px;
  }

  & i {
    color: #000;
    font-size: 20px;
    opacity: 0.7;
  }

  .date-picker {
    /* display: none; */
    position: absolute;
    top: 19px;
    left: 0px;
    box-shadow: 0px 3px 2px #ccc;
    z-index: 1;
  }

  .date-picker::before {
    content: "";
    display: block;
    width: 235px;
    height: 30px;
    background-color: red;
  }

  .fade-in {
    animation: fadeIn 0.6s;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
`;
export default function FormInput() {
  const handlerReplacePage = () => {
    window.location.replace("/search");
  };

  return (
    <StyledForm>
      <label>
        <i className="fa fa-bed"></i>
        <input type="text" placeholder="Where are you going?" />
      </label>
      <label>
        <div className="input-icon">
          <i className="fa fa-calendar"></i>
          <input type="text" placeholder="06/24/2022 to 06/24/2022" />
          <div className="date-picker fade-in">
            <DataPicker />
          </div>
        </div>
      </label>

      <label>
        <i className="fa fa-female"></i>
        <input type="text" placeholder="1 adult - 0 children - 1 room" />
      </label>

      <UIButton onClick={handlerReplacePage} padding="0 20px" margin="0 auto">
        Search
      </UIButton>
    </StyledForm>
  );
}
