import { useState } from "react";
const [state, setState] = useState([
  {
    startDate: new Date(),
    endDate: null,
    key: "selection",
  },
]);

<DateRange
  editableDateInputs={true}
  onChange={(item) => setState([item.selection])}
  moveRangeOnFirstSelection={false}
  ranges={state}
/>;
