import { useState } from "react";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import { styled } from "styled-components";

const StyledDateRange = styled.div`
  /* .input-icon:hover .date-picker {
    display: block;
  } */

  .input-icon {
    display: flex;
    align-items: center;
    position: relative;
  }

  .input-icon input {
    font-size: 16px;
  }

  .date-picker {
    display: ${(props) => (props.diplay === false ? "none" : "block")};
    position: absolute;
    top: 50px;
    left: 0px;
    box-shadow: 0px 3px 5px #ccc;
    z-index: 1000;
  }

  .fade-in {
    animation: fadeIn 0.6s;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
`;
export default function DatePicker() {
  const [dates, setDates] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

  const [showModal, setShowModal] = useState(false);

  const handlerDate = () => {
    let dataDate;
    dates.forEach((item) => {
      var startDate = item.startDate;
      var endDate = item.endDate;

      var startFormattedDate =
        startDate.getMonth() +
        1 +
        "/" +
        startDate.getDate() +
        "/" +
        startDate.getFullYear();
      var endFormattedDate =
        endDate.getMonth() +
        1 +
        "/" +
        endDate.getDate() +
        "/" +
        endDate.getFullYear();
      dataDate = startFormattedDate + " to " + endFormattedDate;
    });
    return dataDate;
  };

  const handlerShowModal = () => {
    setShowModal(true);
  };

  return (
    <StyledDateRange display={showModal}>
      <div className="input-icon">
        <i className="fa fa-calendar"></i>
        <input
          type="text"
          placeholder="06/24/2022 to 06/24/2022"
          value={handlerDate()}
          onClick={handlerShowModal}
        />
        <div className="date-picker fade-in">
          <DateRange
            editableDateInputs={true}
            moveRangeOnFirstSelection={false}
            minDate={new Date()}
            onChange={(item) => setDates([item.selection])}
            ranges={dates}
          />
        </div>
      </div>
    </StyledDateRange>
  );
}
