import React from "react";
import { styled } from "styled-components";

const Div = styled.div`
  & img {
    width: 200px;
    height: 240px;
  }
`;
export default function HotelItem(props) {
  return (
    <Div>
      <img src={props.image_url} alt="" />
      <h3>{props.name}</h3>
      <p>{props.city}</p>
      <p>{props.price}</p>
      <span>
        {props.rate} <p>{props.type}</p>
      </span>
    </Div>
  );
}
