import React from "react";

import styles from "./Button.module.css";

const Button = (props) => {
  const borderBtn = (e) => {
    if (e === true) {
      console.log("aa");
    } else console.log("bb");
  };

  return (
    <button
      type={props.type}
      className={styles.btn}
      onClick={props.onClick}
      addHandlerBorder={borderBtn}
    >
      {props.children}
    </button>
  );
};

export default Button;
