import React, { useState } from "react";
import styles from "./style/NavBarItem.module.css";
import Button from "../../elements/UIButton";
import { styled } from "styled-components";

const D = styled(Button)`
  border: ${(props) => (props.active ? "1px solid #fff" : "none")};
`;
export default function NavBarItem(props) {
  const [active, setActive] = useState(props.active);
  return (
    <div className={styles["nav-bar-item"]}>
      <div className={`${active && styles.btnBorder}`}>
        <D>
          <Button border={active}>
            <i className={`fa ${props.icon}`}></i>
            <p>{props.type}</p>
          </Button>
        </D>
      </div>
    </div>
  );
}
