import React from "react";
import { styled } from "styled-components";

const Div = styled.div`
  border-radius: 10px;
  color: #fff;
  position: realtive;
  & img {
    width: 290px;
    height: 290px;
  }

  & div {
    position: absolute;
    top: 10px;
    color: red;
    z-index: 3;
  }
`;

export default function CityItem(props) {
  return (
    <Div>
      <img src={props.image} alt="city" />
      <div>
        <p>{props.name}</p>
        <p>{props.subText}</p>
      </div>
    </Div>
  );
}
