import React from "react";
import { styled } from "styled-components";

const Div = styled.div`
  & i {
    width: 150px;
    height: 150px;
  }
`;
export default function HotelItem(props) {
  return (
    <Div>
      <img src={props.image} alt="" />
      <h3>{props.name}</h3>
      <p>{props.count}</p>
    </Div>
  );
}
