import React, { useState } from "react";
import { styled } from "styled-components";

export default function NavBarItem(props) {
  const [isValid, setIsValid] = useState(props.active);

  isValid.map((i) => console.log(i));
  const Div = styled.div`
    border: ${(props) => (props.active === true ? "1px solid #fff" : "none")};
    border-radius: 20px;
    color: #fff;
    display: flex;
    align-items: center;
    padding: 8px;
    margin: 20px;

    & i {
      width: 20px;
      height: 20px;
      display: flex;
      align-items: center;
      justify-content: center;
      padding-right: 8px;
    }
  `;

  return (
    <Div>
      <i class={`fa ${props.icon}`}></i>
      <p>{props.type}</p>
    </Div>
  );
}
