import React from "react";
import "./styles/SearchItem.css";
import { styled } from "styled-components";
import Button from "../../../components/elements/UIButton";

const StyledSearchItem = styled.div`
  display: flex;
  border: 1px solid #ccc;
  width: 100%;
  padding: 12px;
  margin-bottom: 25px;
  box-sizing: border-box;

  .search-item--image {
    width: 220px;
    height: 270px;
  }

  .search-item--content1 {
    margin: 0px 20px;
    line-height: 40px;
  }

  .search-item--content1 h2 {
    color: #0071c2;
  }

  .search-item--tag {
    background-color: #008009;
    display: inline;
    padding: 5px;
    border-radius: 5px;
    color: #fff;
  }

  .search-item--description {
    font-weight: bold;
  }
`;

export default function SearchItem(props) {
  return (
    <StyledSearchItem>
      <img src={props.image_url} alt="image-room" />

      <div className="search-item--content1">
        <h2>{props.name}</h2>
        <p className="search-item--distance">{props.distance} from center</p>
        <p className="search-item--tag">{props.tag}</p>
        <p className="search-item--description">{props.description}</p>
        <p className="search-item--type">{props.type}</p>
      </div>
      <div className="search-item--content2">
        <span>
          {props.rate_text} <p>{props.rate}</p>
        </span>
        <p className="search-item--price">${props.price}</p>
        <p>includes taxes and fees</p>
        <Button>See availability</Button>
      </div>
    </StyledSearchItem>
  );
}
